        <div className="p-4 text-center text-slate-500 text-sm">
            Loading conversations...
        </div>
    )}

    {!loading && conversations.length === 0 && (
        <div className="p-4 text-center text-slate-500 text-sm">
            <MessageSquare className="w-8 h-8 mx-auto mb-2 opacity-30" />
