import React, { useState, useEffect, useCallback } from 'react';
import { Upload, FileText, Trash2, CheckCircle, XCircle, Loader2, FolderOpen } from 'lucide-react';
import { documentAPI } from '../services/api';

export const DocumentsPanel = ({ activePersona }) => {
    const [documents, setDocuments] = useState([]);
    const [uploading, setUploading] = useState(false);
    const [isDragging, setIsDragging] = useState(false);
    const [error, setError] = useState(null);

    const loadDocuments = useCallback(async () => {
        try {
            const docs = await documentAPI.list(activePersona.role);
            setDocuments(docs);
        } catch (e) {
            console.error('Failed to load documents:', e);
        }
    }, [activePersona.role]);

    useEffect(() => {
        loadDocuments();
    }, [loadDocuments]);

    const handleUpload = async (files) => {
        setUploading(true);
        setError(null);

    for (const file of files) {
        if (!file.name.toLowerCase().endsWith('.pdf')) {
            setError('Only PDF files are supported');
            continue;
        }

        try {
            await documentAPI.upload(file, activePersona.role);
        } catch (e) {
            setError(`Failed to upload ${file.name}`);
            console.error('Upload error:', e);
        }
    }

    setUploading(false);
    loadDocuments();
};

const handleDelete = async (docId) => {
    if (!confirm('Are you sure you want to delete this document?')) return;

    try {
        await documentAPI.delete(docId, activePersona.role);
        loadDocuments();
    } catch (e) {
        setError('Failed to delete document');
    }
};

const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
};

const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
};

const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);

    const files = Array.from(e.dataTransfer.files).filter(f => f.name.toLowerCase().endsWith('.pdf'));
    if (files.length > 0) {
        handleUpload(files);
    }
};

const handleFileSelect = (e) => {
    const files = Array.from(e.target.files);
    if (files.length > 0) {
        handleUpload(files);
    }
};

const formatFileSize = (bytes) => {
    if (bytes < 1024) return bytes + ' B';
    if (bytes < 1024 * 1024) return (bytes / 1024).toFixed(1) + ' KB';
    return (bytes / (1024 * 1024)).toFixed(1) + ' MB';
};

const formatDate = (dateStr) => {
    return new Date(dateStr).toLocaleDateString('en-US', {
        month: 'short',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
    });
};

return (
    <div className="flex-1 overflow-y-auto p-6 bg-stone-50">
        <h1 className="text-2xl font-bold text-stone-800 mb-6">Documents</h1>

{/* Upload Zone */}
<div
    className={`border-2 border-dashed rounded-2xl p-8 text-center transition-all mb-6 ${isDragging
            ? 'border-violet-500 bg-violet-50'
            : 'border-stone-300 bg-white hover:border-violet-300'
        }`}
    onDragOver={handleDragOver}
    onDragLeave={handleDragLeave}
    onDrop={handleDrop}
>
    <input
        type="file"
        accept=".pdf"
        multiple
        onChange={handleFileSelect}
        className="hidden"
        id="file-upload"
    />

{error && (
    <div className="mb-4 p-3 bg-red-50 border border-red-200 rounded-xl text-red-600 text-sm flex items-center gap-2">
        <XCircle size={16} />
        {error}
    </div>
)}

